import { StyleSheet, Text, View } from "react-native";
import React, { useState } from "react";
import { Octicons } from "@expo/vector-icons";
import { SafeAreaView } from "react-native-safe-area-context";
import Cart from "../components/Cart";
import Wishlist from "../components/Wishlist";
import StepIndicator from "react-native-step-indicator";
import CartProducts from "./ProgressBar/CartProducts";
import DeliveryAddress from "./ProgressBar/DeliveryAddress";
import PaymentScreen from "./ProgressBar/PaymentScreen";
import ConfirmOrder from "./ProgressBar/ConfirmOrder";

const labels = ["Cart", "Delivery Address", "Payment Method", "Confirm Order"];
const customStyles = {
  stepIndicatorSize: 25,
  currentStepIndicatorSize: 30,
  separatorStrokeWidth: 2,
  currentStepStrokeWidth: 3,
  stepStrokeCurrentColor: "#fe7013",
  stepStrokeWidth: 2,
  stepStrokeFinishedColor: "#fe7013",
  stepStrokeUnFinishedColor: "#aaaaaa",
  separatorFinishedColor: "#fe7013",
  separatorUnFinishedColor: "#aaaaaa",
  stepIndicatorFinishedColor: "#fe7013",
  stepIndicatorUnFinishedColor: "#ffffff",
  stepIndicatorCurrentColor: "#ffffff",
  stepIndicatorLabelFontSize: 13,
  currentStepIndicatorLabelFontSize: 13,
  stepIndicatorLabelCurrentColor: "#fe7013",
  stepIndicatorLabelFinishedColor: "#ffffff",
  stepIndicatorLabelUnFinishedColor: "#aaaaaa",
  labelColor: "#999999",
  labelSize: 13,
  currentStepLabelColor: "#fe7013",
};
const renderScreen = [
  <CartProducts />,
  <DeliveryAddress />,
  <PaymentScreen />,
  <ConfirmOrder />,
];

const CartScreen = () => {
  const [currentPosition, setCurrentPosition] = useState(0);
  const nextPage = () => setCurrentPosition(currentPosition + 1);
  const prevPage = () => setCurrentPosition(currentPosition - 1);
  return (
    <SafeAreaView
      style={{
        marginTop: 5,
        flex: 1,
        backgroundColor: "rgba(255,255,240,0.1)",
      }}
    >
      <View
        style={{
          flexDirection: "row",
          justifyContent: "space-between",
          width: "92%",
          marginVertical: 16,
        }}
      >
        <View>
          <Text
            style={{
              fontSize: 20,
              fontWeight: "600",
              marginLeft: 20,
              letterSpacing: 1.2,
            }}
          >
            Cart
          </Text>
        </View>
        <View style={{ flexDirection: "row", alignItems: "center", gap: 20 }}>
          <Octicons name="bell" size={20.5} color="rgba(0,0,0,0.7)" />
          <Wishlist />
          <Cart />
        </View>
      </View>
      <View>
        <StepIndicator
          customStyles={customStyles}
          currentPosition={currentPosition}
          labels={labels}
        />
        {renderScreen[currentPosition]}
      </View>
    </SafeAreaView>
  );
};

export default CartScreen;

const styles = StyleSheet.create({});
