import {
  Pressable,
  ScrollView,
  StyleSheet,
  Text,
  View,
  Image,
  useWindowDimensions,
} from "react-native";
import React from "react";
import {
  Ionicons,
  MaterialIcons,
  FontAwesome5,
  Octicons,
  Feather,
} from "@expo/vector-icons";
import { SafeAreaView } from "react-native-safe-area-context";
import { deals } from "../data/DealsData";
import { selectCartItems } from "../redux/reducers/cartSlice";
import { Selector, useSelector } from "react-redux";
import Cart from "../components/Cart";
import Wishlist from "../components/Wishlist";
import AddtoCart from "../components/AddtoCart";
import Like from "../components/Like";
import { useNavigation } from "@react-navigation/native";
import CustomStarRating from "../components/CustomStarRating";

const CartScreen = () => {
  const navigation = useNavigation();
  const screenWidth = useWindowDimensions("window").width;
  const cartItems = useSelector(selectCartItems);
  const handleProductPress = (productId) => {
    navigation.navigate("Product", { id: productId });
  };
  return (
    <SafeAreaView
      style={{
        marginTop: 5,
        flex: 1,
        backgroundColor: "rgba(255,255,240,0.1)",
      }}
    >
      <View
        style={{
          flexDirection: "row",
          justifyContent: "space-between",
          width: "92%",
          marginVertical: 16,
        }}
      >
        <View>
          <Text
            style={{
              fontSize: 20,
              fontWeight: "600",
              marginLeft: 20,
              letterSpacing: 1.2,
            }}
          >
            Cart
          </Text>
        </View>
        <View style={{ flexDirection: "row", alignItems: "center", gap: 20 }}>
          <Octicons name="bell" size={20.5} color="rgba(0,0,0,0.7)" />
          <Wishlist />
          <Cart />
        </View>
      </View>
      <ScrollView>
        <View
          style={{
            marginHorizontal: screenWidth * 0.05,
            width: screenWidth * 0.9,
          }}
        >
          {cartItems.map((item, index) => (
            <Pressable
              key={index}
              style={{}}
              onPress={() => handleProductPress(item.id)}
            >
              <View
                style={{
                  backgroundColor: "white",
                  borderRadius: 10,
                  width: screenWidth * 0.9,
                  marginVertical: 5,
                }}
              >
                <View style={{ flexDirection: "row" }}>
                  <Image
                    style={{
                      width: 105,
                      height: 105,
                      borderRadius: 5,
                      margin: 10,
                    }}
                    source={item.image}
                  />
                  <View>
                    <View
                      style={{
                        paddingHorizontal: 3,
                        paddingVertical: 5,
                        maxWidth: screenWidth * 0.5,
                        marginTop: 2,
                        height: 84,
                      }}
                    >
                      <Text
                        numberOfLines={2}
                        style={[
                          styles.text,
                          {
                            fontSize: 14,
                            fontWeight: "500",
                            overflow: "hidden",
                            marginVertical: 0,
                          },
                        ]}
                      >
                        {item.title}
                      </Text>
                      <View style={{ flexDirection: "row", marginVertical: 2 }}>
                        <Text style={{ fontWeight: "500" }}>₹{item.price}</Text>
                        <Text
                          style={{
                            textDecorationLine: "line-through",
                            fontSize: 12,
                            color: "grey",
                            fontWeight: "300",
                            marginHorizontal: 10,
                          }}
                        >
                          ₹{item.oldPrice}
                        </Text>
                        <Text style={{ toFixed: 0 }}>
                          {(item.price * 100) / item.oldPrice}%
                        </Text>
                      </View>
                      <View style={{ width: 50, marginVertical: 2 }}>
                        <CustomStarRating
                          rating={item.rating}
                          starSize={10}
                          fullStarColor={"rgba(255,167,15,0.9)"}
                        />
                      </View>
                      <View style={{ bottom: -11 }}>
                        <AddtoCart
                          dealId={item.id}
                          customStyle={{}}
                          counterCustomStyle={{ marginRight: 15 }}
                          width={180}
                          showCounter={true}
                          showRemove={true}
                        />
                      </View>
                    </View>
                  </View>
                </View>
              </View>
              <View style={{ position: "absolute", top: 20, left: 80 }}>
                <Like Iconsize={20} dealId={item.id} />
              </View>
            </Pressable>
          ))}
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

export default CartScreen;

const styles = StyleSheet.create({});
